var $window = $(window);
var windowsize = $window.width();
// <!--Event Modal Trigger-->
$(".c1").click(function() {
    //Insert JSON data to modal
    var $class = $(this).removeClass('c1').attr('class');
    $(this).addClass('c1');
    EventCategory.forEach(
        function(obj) {
            if (obj.name == $class) {
                $('.modal-head h2').remove();
                $('.modal-head').append("<h2></h2>");
                $('.modal-head h2').append(obj.name.toUpperCase());
                var list = " ";
                var count = 0;
                $('.event').remove();
                $('.categories').append('<div class="event"></div>');
                obj['events'].forEach(
                    function(event) {
                        var EventName = event.name;
                        EventName = EventName.split(" ").join("");
                        list = list + '<li id="' + EventName + '"><img src="'+ event.thumb +'"></img><div>' +'<p>' +event.name +'</p>'+ '<span class="link">'+ event.link +'</span>'+'</div></li>';
                        $('.event').append('<div class="' + EventName + '">' + event.desc + '<span class="link">'+ event.link +'</span>'+'</div>');
                        if (count > 0) {
                            $('.event div').hide();
                        }
                        count += 1;
                    }
                );
                $('.event div').eq(0).show();
                $('ul#event-list').remove();
                $('.event-names').append('<ul id="event-list"></ul>');
                $('ul#event-list').append(list);
            }
        }
    );
    $('#event-list li').click(function() {
        $('.event div').hide();
        var id = $(this).attr('id');
        if (windowsize < 720) {
            $('.event-names').hide();
            $('.event').fadeIn();
            $('.' + id).fadeIn();
            $('.modal-back').fadeIn();
        } else {
            $('.event-names').fadeIn();
            $('.' + id).fadeIn();
        }
    });
    $(".overlay").css("display", "flex").hide().fadeIn();
});
$(".modal-back").click(function() {
    if (windowsize < 720) {
        $('.event-names').fadeIn();
        $('.event').hide();
        $('.event div').hide();
        $(".modal-back").hide();
    }
});
$(".modal-close").click(function() {
    $(".overlay").css("display", "none");
    if (windowsize < 720) {
        $('.event-names').fadeIn();
        $('.event').hide();
        $('.event div').hide();
    } else {
        $('.event div').hide();
        $('.event-names').show();
        $('.event').show();
        $('.event div').eq(0).show();

    }
});

